'use client'

import { ProDescriptions } from '@ant-design/pro-components'
import { getSysUserInfo } from '@/services/system/user'
import { Image } from 'antd'
import { useState, useEffect, forwardRef } from 'react'
type ModalModifyFormDataProps = {
  currentRow: any
}

const ModalModifyForm: any = (props: ModalModifyFormDataProps, ref) => {
  const { currentRow } = props
  const [loading, setLoading] = useState(false)
  const [detailCurrentRow, setDetailCurrentRow] = useState({ ...currentRow })

  useEffect(() => {
    setLoading(true)
    if (currentRow.id) {
      // 编辑获取根据id用户信息
      getSysUserInfo({ id: currentRow.id }).then((res: any) => {
        if (res.code === 200) {
          setDetailCurrentRow({ ...currentRow, ...res.data })
        } else {
          setDetailCurrentRow({ ...currentRow })
        }
        setLoading(false)
      })
    } else {
      setDetailCurrentRow({ ...currentRow })
      setLoading(false)
    }
  }, [currentRow?.id])

  return (
    <ProDescriptions size="small" column={1} loading={loading} bordered>
      <ProDescriptions.Item label="头像">
        <Image
          alt="头像"
          width={40}
          src={detailCurrentRow.avatarUrl}
          fallback="data:image/png;base64,"
        />
      </ProDescriptions.Item>
      <ProDescriptions.Item label="登录名称">
        {detailCurrentRow.loginName}
      </ProDescriptions.Item>
      <ProDescriptions.Item label="用户名称">
        {detailCurrentRow.userName}
      </ProDescriptions.Item>
      <ProDescriptions.Item label="联系方式">
        {detailCurrentRow.userPhone}
      </ProDescriptions.Item>
      <ProDescriptions.Item label="显示顺序">
        {detailCurrentRow.orderNum}
      </ProDescriptions.Item>
      <ProDescriptions.Item label="是否删除">
        <div
          className={`${
            detailCurrentRow.isDelete ? 'text-red-500' : 'text-green-400'
          }`}
        >
          {detailCurrentRow.isDelete ? '是' : '否'}
        </div>
      </ProDescriptions.Item>
      <ProDescriptions.Item label="创建时间">
        {detailCurrentRow.createTime}
      </ProDescriptions.Item>
      <ProDescriptions.Item label="更新时间">
        {detailCurrentRow.updateTime}
      </ProDescriptions.Item>
      <ProDescriptions.Item label="角色">
        {detailCurrentRow.roleList?.map((item) => item.roleName).join(' | ')}
      </ProDescriptions.Item>
    </ProDescriptions>
  )
}
export default forwardRef(ModalModifyForm)
